var date = new Date(msg.payload);
var date_str = date.toISOString().slice(0,10) + " " + date.toISOString().slice(11,-5);
msg.payload = date_str
msg.className = "greyback"
return msg;

var url = msg.payload
var index1 = url.indexOf(":",0)
var index2 = url.indexOf(":", index1+1)
msg.payload = url.slice(index1+3,index2) //IP
return msg;

var usb2 = {payload:"",topic:"USB 2.0",className: "greyback"};
var usb3 = {payload:"",topic:"USB 3.0",className: "greyback"};
var string = msg.payload
const index1 = string.indexOf("\n",0)
usb3.payload = string.slice(0,index1)
usb2.payload = string.slice(index1+1,-1)

if(usb3.payload == "power"){usb3.className = "green"}
if(usb2.payload == "power"){usb2.className = "green"}

return [usb2, usb3]

var msg1,msg2,msg3;

l = msg.payload.split(/(\s+)/).filter( function(e) { return e.trim().length > 0; } );
msg1 = {topic: 'Total', payload: Math.round(l[1] / 1000)};
msg2 = {topic: 'Free', payload: Math.round(l[3] / 1000)};
msg3 = {topic: 'Used%', payload: l[4].slice(0, -1)};

return [msg1, msg2, msg3];

var d = Math.floor(msg.payload.uptime / 86400).toString();
var h = Math.floor((msg.payload.uptime % 86400) / 3600).toString().padStart(2, '0');
var m = Math.floor(((msg.payload.uptime % 86400) % 3600) / 60).toString().padStart(2, '0');
var s = Math.round(((msg.payload.uptime % 86400) % 3600) % 60).toString().padStart(2, '0');
msg.payload = d + ' ' + h + ':' + m + ':' + s;
msg.className = "greyback"
return msg;

if      (msg.payload == "1\n"){
    msg.payload = "On"
    msg.className = "green"
}
else if (msg.payload == "0\n"){
    msg.payload = "Off"
    msg.className = "greyback"
}

return msg;

